var Candle=function(){this._about="This project is based on candle.js",this._canvas=document.getElementById("display"),this._canvas.width=640,this._canvas.height=480,this._canvas.style.background="#f0f0f0",this._entities=[],this._images=[],this._sounds=[],this._scriptsPath="/scripts/",this._scripts=["input.js","loop.js","entity.js","camera.js"]};Candle.prototype.about=function(){console.log(this._about)},Candle.prototype.init=function(t){this._scripts.push.apply(this._scripts,t)},Candle.prototype.getScripts=function(){var t=this,s=$.map(this._scripts,function(s){return t._scriptsPath+s});return s},Candle.prototype.loadScripts=function(t,s){var n=0,i=function(){++n===t.length&&s()};t.forEach(function(t){loadJS(t,i)})},Candle.prototype.load=function(){this.about();var t=this.getScripts();this.loadScripts(t,this.wolf3d.bind(this))},Candle.prototype.getCanvas=function(){return this._canvas},Candle.prototype.wolf3d=function(){var t=new Input,s=new Loop,n=new Entity;n.control=function(){var s=t.states,n=s.forward-1*s.backward,i=s.right-1*s.left;this.setOmega(i),this.setSpeed(n)};var i=this.getCanvas(),a=new Camera(i,n);s.start(function(t){n.control(),n.motion(t),a.render(t)})};