var Plane=function(t){this._size=t,this._size2=t*t,this._grids=new Uint8Array(t*t),this.randomize(),this.print()};Plane.prototype={get size(){return this._size},get size2(){return this._size2}},Plane.prototype.getGrid=function(t,i){var e=this.index(t,i);return-1!==e?this._grids[e]:0},Plane.prototype.setGrid=function(t,i,e){var n=this.index(t,i);-1!==n&&(this._grids[n]=e)},Plane.prototype.index=function(t,i){var e=t*this.size+i;return e>0&&e<this.size2?e:-1},Plane.prototype.randomize=function(){for(var t=0;t<this.size2;t++){var i=Math.random()<.3?111:0;this._grids[t]=i}},Plane.prototype.print=function(){function t(t,i){var e="      "+t;return e.substr(e.length-i)}for(var i="",e=0;e<this.size;e++){for(var n=0;n<this.size;n++)i+=t(this.getGrid(e,n),4);i+="\n"}console.log(i)};